INSERT INTO public.ayudas_ref VALUES ('069f7c46-7c2e-4917-8356-f9b56daeaba6_4', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "24-1"}]', NULL, '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": {"texto": "declaración responsable. Este requisito será exigible para las solicitudes de ayuda presentadas a partir del 1 de agosto de 2024.", "fragment": "7-1", "id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha"}}]', NULL, '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "22-1"}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "5-4"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "5-4"}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "7-1"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "7-1"}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "36-1"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por empresa será de 175.000 euros.", "fragment": "10-2"}}]', NULL, '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "10-3"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "10-3"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": {"texto": "Los proyectos se financiarán mediante una Ayuda Parcialmente Reembolsable, compuesta por un tramo reembolsable y un tramo no reembolsable, sujeto a disponibilidad de fondos, con una cobertura de hasta el 85% del presupuesto total aprobado por el Consejo de Administración del CDTI.", "fragment": "11-2", "id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha"}}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "11-2"}]', '[{"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "24-5"}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "26-5"}, {"id": "069f7c46-7c2e-4917-8356-f9b56daeaba6_ficha", "fragment": "24-5"}, {"id": "069f7c46-7c2e-