INSERT INTO public.ayudas_ref VALUES ('3246b1c8-5028-403f-8d33-50472f3f18d3_2', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "Resolución de 21 de marzo de la Presidencia del Centro para el Desarrollo Tecnológico y la Innovación, E.P.E. por la que se aprueba la convocatoria para el año 2023 del procedimiento de concesión de ayudas...", "fragment": "1-1", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha"}}]', NULL, '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "16-4"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "16-4"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "5-3"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "2-3"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "6-6"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "Artículo 3. Beneficiarios ............................................................................................................ 8", "fragment": "1-3", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha"}}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "1-2"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "6-5"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "6-6"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "18-3"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "6-3"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "6-6"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "proyectos tendrán una duración de hasta tres anualidades, de forma que deberán finalizar el 30 de junio de 2025.", "fragment": "7-1"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "Los proyectos tendrán una duración de hasta tres anualidades, de forma que deberán finalizar el 30 de junio de 2025.", "fragment": "6-3"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "tendrán una duración máxima de tres años.", "fragment": "4-6"}}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "7-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "11-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "11-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "53-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "7-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "11-1"}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "33-3"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "32-5"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "34-8"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "fraccionado, de forma anticipada, a cuenta, y pago con posterioridad\na la realización de la actividad objeto de ayuda.\n3. Tras dictarse la resolución de concesión definitiva, el representante de la\nagrupación podrá solicitar el pago anticipado de hasta el 60% de la\nsubvención concedida para la primera anualidad. El pago anticipado\nde las siguientes anualidades de hasta el 60 %, queda condicionado a la\npresentación de la documentación justificativa de la anualidad anterior.", "fragment": "32-5", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "6. El pago queda supeditado a la disponibilidad presupuestaria por parte\ndel CDTI.\n7. De conformidad con el artículo 28.4 de la Orden CIN/373/2022, de 26 de\nabril se podrá acordar, como medida cautelar a raíz de la intervención\nde las autoridades de control competentes, nacionales o europeas, la\nretención del pago a la que se alude en el artículo 88.3 c) de Reglamento\nde desarrollo de la Ley 38/2003, de 17 de noviembre. Dicha retención se", "fragment": "33-5", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "el pago podrá hacerse en forma de pago único o fraccionado, anual o plurianual, de forma anticipada, a cuenta, con posterioridad a la realización de la actividad objeto de ayuda, o en una combinación de las formas anteriormente citadas.", "fragment": "34-7"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "Los pagos a cuenta deberán estar adaptados al ritmo de ejecución de la actuación, abonándose por cuantía equivalente a la justificación presentada anualmente por el beneficiario.", "fragment": "34-8"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "fraccionado, de forma anticipada, a cuenta, y pago con posterioridad a la realización de la actividad objeto de ayuda. Tras dictarse la resolución de concesión definitiva, el representante de la agrupación podrá solicitar el pago anticipado de hasta el 60% de la subvención concedida para la primera anualidad. El pago anticipado de las siguientes anualidades de hasta el 60 %, queda condicionado a la presentación de la documentación justificativa de la anualidad anterior.", "fragment": "32-5"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": {"texto": "El pago de la ayuda se realizará al representante de la agrupación, quien se responsabilizará de la transferencia a cada entidad beneficiaria de los fondos que les correspondan según el reparto establecido en la resolución de concesión. El representante de la agrupación deberá transferir los fondos al resto de la agrupación, en un plazo máximo de un mes a contar desde la fecha de recepción del pago de la ayuda, ya sea pago anticipado o pago.", "fragment": "33-3"}}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "43-9"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "47-2"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "47-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "La definición de pequeña y mediana empresa (PYME) es la establecida en el anexo I del Reglamento (UE) n.º 651/2014 de la Comisión, de 17 de junio de 2014.", "fragment": "47-1", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases"}}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "Los límites de ayuda establecidos en la normativa europea que sea de aplicación.", "fragment": "16-1", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases"}}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "4-4"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "39-6"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "5-3"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "5-2"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "10-1"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": {"texto": "b) Hasta 1,2 millones de euros en el caso de empresas establecidas en zonas asistidas que cumplan las condiciones del artículo 107, apartado 3, letra c) del Tratado de Funcionamiento de la Unión Europea. c) Hasta 1,6 millones de euros en el caso de empresas establecidas en zonas asistidas que cumplan las condiciones del artículo 107, apartado 3, letra a), de dicho Tratado.", "fragment": "18-3", "id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases"}}]', '[{"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "12"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "17"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "12"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_ficha", "fragment": "11-2"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "17"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "16-4"}, {"id": "3246b1c8-5028-403f-8d33-50472f3f18d3_bases", "fragment": "18-1"}, {"id": "3246b1c8-5028-403f-8