INSERT INTO public.ayudas_ref VALUES ('3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_1', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "14-1"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "12-3"}]', NULL, '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "12-3"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "12-1"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "7-1"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "El CDTI evaluará el carácter innovador, impacto de la propuesta y la viabilidad técnica y financiera de la propuesta...", "fragment": "12-6", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "4-2"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "12-1"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "7-1"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-6"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-6"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "Los proyectos de innovación tecnológica susceptibles de ayuda a través de la línea LIC tendrán una duración mínima de 9 meses y máxima de 24 meses, con un único hito, sin perjuicio de la aprobación de posibles prórrogas de ejecución.", "fragment": "9-1", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-1"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-3"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "10-1"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "10-1"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-3"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "14-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "16-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "14-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "16-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "14-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "16-2"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por proyecto es de 175.000 euros y el máximo de 6 millones de euros, si bien, en ningún caso, se podrán superar los límites máximos de ayuda que se establecen en la normativa vigente de las ayudas de minimis concedidas a una única empresa.", "fragment": "8-6", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "en la normativa vigente de las ayudas de minimis concedidas a una única empresa. El importe total de las ayudas de minimis concedidas por un Estado miembro a una «única empresa» no excederá de 300.000€ durante cualquier período de tres años13.", "fragment": "11-5", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "de la Comisión, de 13 de diciembre de 2023, relativo a la aplicación de los artículos 107 y 108 del Tratado de Funcionamiento de la Unión Europea a las ayudas de minimis.", "fragment": "17-5", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "11-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-6"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "4-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "17-5"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "12-6"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "El importe total de las ayudas de minimis concedidas por un Estado miembro...", "fragment": "11-5"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "Reglamento UE 2023/2831, ...relativo a la aplicación de los artículos 107 y 108 del Tratado de Funcionamiento de la Unión Europea a las ayudas de...", "fragment": "4-4"}}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-3"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-3"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "9-4"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "debiendo cubrir el 25% del presupuesto, cuando la aportación CDTI sea del 75% o del 15% cuando la aportación CDTI sea del 85%, a través de recursos propios", "fragment": "9-5", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "Estos proyectos se financiarán mediante una Ayuda Parcialmente Reembolsable con una cobertura de hasta el 75% del presupuesto total aprobado por el Consejo de Administración del CDTI. En caso de que el proyecto de innovación sea cofinanciado con cargo a FEDER... la cobertura podrá alcanzar hasta el 85% del presupuesto total aprobado.", "fragment": "9-3", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "A efectos de este cálculo, la cobertura máxima será del 75% del presupuesto total aprobado por el Consejo de Administración. El incremento de cobertura financiera hasta el 85% no genera tramo no reembolsable adicional, es decir, se seguirá calculando sobre el 75% del presupuesto total.", "fragment": "11-3", "id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha"}}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": {"texto": "El tramo no reembolsable de la ayuda se calcula sobre su cobertura financiera. A efectos de este cálculo, la cobertura máxima será del 75% del presupuesto total aprobado por el Consejo de Administración. El incremento de cobertura financiera hasta el 85% no genera tramo no reembolsable adicional, es decir, se seguirá calculando sobre el 75% del presupuesto total.", "fragment": "11-3"}}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "2-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "2-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "3-3"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "2-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "3-3"}]', '[{"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "6-4"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "6-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "6-4"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "6-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "1-2"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "7-4"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "7-5"}, {"id": "3a2d7d1e-2ed1-457a-8c20-9d35faf6b3a2_ficha", "fragment": "8-2"}]');
