INSERT INTO public.ayudas_ref VALUES ('774cdf97-1d6f-41f9-8e35-fd366d694d26_1', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-2"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-4"}]', NULL, '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-7"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "2-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-5"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "2-5"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-7"}]', NULL, '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "33-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "35-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "35-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-2"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "33-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-4"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-6"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "El Tratado de Funcionamiento de la UE (TFUE)3 prevé en su artículo 107.3.b) que podrán considerarse compatibles con el mercado interior las ayudas para la realización de proyectos importantes de interés común europeo (IPCEI por sus siglas en inglés).", "fragment": "4-2", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "Notificación de ayudas de Estado ante la Comisión Europea (COM) y coordinado entre todos los EE.MM. participantes...", "fragment": "8-6", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-2"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-6"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-2"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "2-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-2"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "el nivel máximo de ayuda [...] la intensidad de la ayuda podría alcanzar hasta el 100 % de los costes subvencionables, si bien el beneficiario debe participar en la cofinanciación del proyecto.", "fragment": "5-1", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "33-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-4"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "3-6"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "8-5"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "4-5"}]', '[{"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "Eligible costs only cover costs made for the purpose and the time span of the IPCEI. Use “Table 4: Summary of eligible costs“ below to provide a general overview.", "fragment": "26-1", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "Table 4: Summary of eligible costs [EUR]. Construction of buildings / facilities, Investment costs, Personnel costs, Subcontract costs, Materials, supplies and others, Total eligible costs.", "fragment": "27-1", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "Tabla: Construction of buildings / facilities | Investment costs | Personnel costs | Subcontract costs | Materials, supplies and others.", "fragment": "28", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": {"texto": "El nivel máximo de ayuda de Estado al potencial IPCEI (financiación pública) se determinaría respecto al déficit de financiación identificado en relación con los costes subvencionables.", "fragment": "5-1", "id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha"}}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "28"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "27-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "26-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-7"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "26-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "27-1"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "28"}, {"id": "774cdf97-1d6f-41f9-8e35-fd366d694d26_ficha", "fragment": "5-1"}]');
INSERT INTO public.ayudas_ref VALUES ('814e5904-cb6b-4973-a9ec-a892e8628202_1', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "El Consejo de Administración del CDTI es el órgano competente para la aprobación o denegación de las ayudas.", "fragment": "15-1", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "sede electrónica de CDTI dentro del apartado ''Firma Electrónica''.", "fragment": "12-6", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', NULL, '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "13-1"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La presentación de solicitudes, a través de la sede electrónica de CDTI, se podrá realizar de forma continuada a lo largo de todo el año, hasta el agotamiento de los fondos disponibles y con los límites temporales establecidos en el Programa Operativo (FEMPA).", "fragment": "13-1", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "Tiene por objeto asegurar que las operaciones elegibles por el FEMPA vayan dirigidas al logro de los objetivos definidos en el Programa.", "fragment": "20-2", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "3-5"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "13-1"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "6-1"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por proyecto es de 175.000 euros y el máximo asciende a 9.500.000 euros.", "fragment": "10-3", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por proyecto es de 175.000 euros y el máximo asciende a 9.500.000 euros.", "fragment": "10-3"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "Los proyectos susceptibles de ayuda a través de la línea LIF tendrán una duración mínima de 9 meses y máxima de 24 meses, con un único hito, sin perjuicio de la aprobación de posibles prórrogas de ejecución.", "fragment": "10-4", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "10-4"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "10-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "11-2"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "10-5"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "17-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "11-4"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "El desembolso de la ayuda tendrá lugar tras la certificación del hito único del proyecto, debiendo presentar una auditoría de gastos, realizada por un auditor inscrito como ejerciente en el en Registro Oficial de Auditores de Cuentas (ROAC), con carácter previo a dicha certificación.", "fragment": "17-5", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "17-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "17-6"}]', NULL, '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "3-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "10-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La presentación de solicitudes, a través de la sede electrónica de CDTI, se podrá realizar de forma continuada a lo largo de todo el año, hasta el agotamiento de los fondos disponibles y con los límites temporales establecidos en el Programa Operativo (FEMPA).", "fragment": "13-1", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "9-1"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La ayuda que se conceda nunca podrá ser superior a la ayuda solicitada por la empresa para el proyecto, ni superar el 75% de intensidad de ayuda máxima a través de la línea LIF5.", "fragment": "11-2", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La intensidad máxima de ayuda es de hasta el 85% para los proyectos que se desarrollen en Canarias.", "fragment": "11-5", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "11-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La ayuda que se conceda nunca podrá ser superior a la ayuda solicitada por la empresa para el proyecto, ni superar el 75% de intensidad de ayuda máxima a través de la línea LIF5. En ningún caso se podrán superar los límites de intensidad de ayuda establecidos en el Reglamento (UE) 2021/1139.", "fragment": "11-2", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "10-6"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "11-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "Estos proyectos se financiarán mediante una Ayuda Parcialmente Reembolsable, compuesta por un tramo reembolsable y un tramo no reembolsable, sujeto a disponibilidad de fondos, con una cobertura de hasta el", "fragment": "10-5"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "reembolsable, sujeto a disponibilidad de fondos, con una cobertura de hasta el 85% del presupuesto total aprobado por el Consejo de Administración del CDTI.", "fragment": "10-6"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La ayuda que se conceda nunca podrá ser superior a la ayuda solicitada por la empresa para el proyecto, ni superar el 75% de intensidad de ayuda máxima a través de la línea LIF5.", "fragment": "11-2", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": {"texto": "La intensidad máxima de ayuda es de hasta el 85% para los proyectos que se desarrollen en Canarias.", "fragment": "11-5", "id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha"}}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "5-1"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "5-4"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "f. Agrupaciones de interés económico, UTES, sociedades de inversión mobiliarias y patrimoniales."}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "5-2"}]', '[{"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "8-2"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "8-3"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "8-2"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "8-3"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "9-5"}, {"id": "814e5904-cb6b-4973-a9ec-a892e8628202_ficha", "fragment": "8-2"}]');
INSERT INTO public.ayudas_ref VALUES ('8c30797b-0ff0-47b6-9b56-abb534ad2159_1', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "2-7"}]', NULL, '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "12-7"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "12-6"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "1. El plazo de presentación de las solicitudes de ayuda comenzará 9 de abril y finalizará el 9 de mayo de 2025 a las 12:00 horas del mediodía, hora peninsular.", "fragment": "12-7", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "2-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "4-6"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "1-2"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "12-7"}]', NULL, '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "5-6"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "La fecha límite de finalización de los proyectos será el 31 de diciembre de 2028, incluyendo posibles prórrogas.", "fragment": "5-7"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "En ningún caso podrá sobrepasar como fecha de finalización del proyecto el 31 de diciembre de 2028.", "fragment": "18-2"}}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Las ayudas se concederán bajo la modalidad de subvenciones.", "fragment": "7-4", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Se concederá el 70 % del presupuesto financiable como ayuda, siempre que el importe máximo de la ayuda no exceda de 2,5 millones de euros por beneficiario y por proyecto.", "fragment": "7-5", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Las ayudas se concederán de forma directa, conforme al artículo 28.1 de la Ley 38/2003, de 17 de noviembre, General de Subvenciones...", "fragment": "8-1"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Estas subvenciones se concederán en régimen de concesión directa de conformidad con la disposición adicional undécima, apartado 7, de la Ley 14/2011...", "fragment": "4-2"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-4"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "22-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "23-2"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "27-5"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "22-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "26-3"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "22-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "23-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "23-3"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Reglamento (UE) n.º 651/2014, de 17 de junio de 2014 y, y en las normas del Reglamento (UE) 2021/695 de 28 de abril de 2021 Horizonte Europa (HE) y, en concreto en el artículo 48 del mismo. 3. Se concederá el 70 % del presupuesto financiable como ayuda, siempre que el importe máximo de la ayuda no exceda de 2,5 millones de euros por beneficiario y por proyecto.", "fragment": "7-5"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Reglamento (UE) nº 651/2014, de 17 de junio de 2014, por el que se declaran determinadas categorías de ayuda compatibles con el mercado interior en aplicación de los artículos 107 y 108 del Tratado. Las actividades subvencionables en la convocatoria del Acelerador del ECI del programa Horizonte Europa son actividades de investigación y desarrollo (nivel TRL 6 a 8) y de despliegue (nivel TRL 9). Dado que estas últimas rebasan el alcance", "fragment": "5-5"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "31-2"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "2-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "3-9"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "proyecto ha sido apoyado por el Ministerio de Ciencia, Innovación y Universidades.\n3. Al tratarse de subvenciones cofinanciadas con fondos europeos de Desarrollo Regional, a, los beneficiarios deberán cumplir las obligaciones, relativas a los Fondos FEDER. A tal efecto, deberán atenerse a lo siguiente:\na. Cumplir la legislación nacional y comunitaria en materia de subvenciones\ny ayudas de Estado.\nb. Observar las normas de subvencionabilidad nacionales...", "fragment": "20-1", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "y ayudas de Estado.\nb. Observar las normas de subvencionabilidad nacionales (Orden HFP/1414/2023, de 27 de diciembre, por la que se aprueban las normas\nsobre los gastos subvencionables de los programas financiados por el Fondo Europeo de Desarrollo Regional para el período 2021-2027) y comunitarias (Reglamento (UE) 2021/1060 y Reglamento (UE) 2021/1058).", "fragment": "20-2", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "CDTI, E.P.E. - Cid, 4 28001 Madrid (España) www.cdti.es", "fragment": "42-7", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Se concederá el 70 % del presupuesto financiable como ayuda, siempre que el importe máximo de la ayuda no exceda de 2,5 millones de euros por beneficiario y por proyecto.", "fragment": "7-5", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": {"texto": "Se concederá el 70 % del presupuesto financiable como ayuda, siempre que el importe máximo de la ayuda no exceda de 2,5 millones de euros por beneficiario y por proyecto.", "fragment": "7-5", "id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha"}}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-6"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "7-5"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-5"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "18-6"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "6-5"}]', '[{"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "8-3"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "39-3"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "9-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "9-2"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "8-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "8-6"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "9-3"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad2159_ficha", "fragment": "9-4"}, {"id": "8c30797b-0ff0-47b6-9b56-abb534ad