INSERT INTO public.ayudas_ref VALUES ('cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_1', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "23-5"}]', NULL, '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "20-4"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "20-4"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "transferencia de conocimiento y fomentar la colaboración entre instituciones y\nagentes del Sistema Español de Ciencia, Tecnología e Innovación (SECTI, en\nadelante), con una atención especial a la colaboración público-privada. En\nparticular, el Subprograma “Para Impulsar la Colaboración en y entre los\nSectores Público y Privado”, en cuyo marco se encuadran las ayudas objeto de\nesta convocatoria, contempla entre sus actuaciones la financiación de", "fragment": "2-4", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "10-1"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "20-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "27-3"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-1"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "6-2"}]', NULL, '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "Duración: los proyectos deberán ser plurianuales, de modo que parte de estos deberá realizarse en el año 2025. En todo caso, se deberá solicitar ayuda para 2025.", "fragment": "7-2"}}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "e. Duración: los proyectos deberán ser plurianuales, de modo que parte de estos deberá realizarse en el año 2025. En todo caso, se deberá solicitar ayuda para 2025. Los proyectos deberán finalizar el 31 de diciembre de 2027 o el 30 de junio de 2028, sin perjuicio de las posibles prórrogas que se autoricen, en su caso.", "fragment": "7-2", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "No obstante, el plazo máximo de ampliación del periodo de ejecución total de la actuación subvencionada será de seis meses para los", "fragment": "28-7", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "Las ayudas de esta convocatoria consistirán en subvenciones.", "fragment": "12-3", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "El importe de la ayuda concedida, en su caso, será igual o inferior a la ayuda solicitada en la presente convocatoria, en términos de subvención bruta equivalente.", "fragment": "44-2", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "12-3"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "14"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "12-3"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "34-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "35-2"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "35-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "34-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "35-2"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "40-1"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "34-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "35-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "35-2"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "El límite de intensidad de ayuda para cada proyecto y beneficiario será una media ponderada en función del reparto de presupuesto entre cada tipo de actividad (investigación industrial o desarrollo experimental) y la intensidad máxima de ayuda que corresponda al beneficiario y a la actividad.", "fragment": "15-3", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-5"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-5"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "5-5"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "12-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "16"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "15-1"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "12-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "14"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "15-1"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "16"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "14"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "16"}]', '[{"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "12-4"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": "14"}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "la ayuda parcialmente reembolsable se regirá por su normativa específica (''Ficha de Proyectos de I+D'' - Proyectos de I+D de Cooperación Nacional), disponible en la", "fragment": "44-1", "id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha"}}, {"id": "cffe1e01-8e8e-4a51-a63d-11cd5519b4b0_ficha", "fragment": {"texto": "el importe de la ayuda concedida, en su caso, será igual o inferior a la ayuda solicitada en la presente convocatoria, en términos de subvención bruta equivalente.", "fragment": "44-2", "id": "cffe1e01-8e8e-4a51-a63d-