INSERT INTO public.ayudas_ref VALUES ('e568eaad-d004-4a6d-94a5-27ef138d0eca_1', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "3-3"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "3-4"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "3-5"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "29-4"}]', NULL, '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "10-1"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "10-1"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Artículo 1.Objeto de la convocatoria\nLa presente Resolución ... aprueba la convocatoria del año 2025 de ayudas para nuevos proyectos empresariales de empresas innovadoras", "fragment": "4-1"}}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "apoyar la creación de nuevas empresas innovadoras ... se primará con una mayor intensidad de ayuda a aquellas propuestas que incluyan la contratación de doctores y proyectos liderados por mujeres.", "fragment": "2-4"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Artículo 3. Entidades beneficiarias.", "fragment": "1-2", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Artículo 4. Requisitos adicionales de las entidades beneficiarias.", "fragment": "1-3", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "por lo previsto enel artículo 34 delaOrden CNU/161/2025, de 7 de febrero.", "fragment": "29-2", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-3"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-4"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "importe máximo de subvención de 325.000 euros por entidad beneficiaria", "fragment": "7-3"}}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "El presupuesto máximo financiable de la actuación deberá ser de 750.000 euros", "fragment": "7-4"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-4"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "17-3"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-1"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "8-3"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "39-2"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-1"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-2"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-3"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "19-4"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Tras dictarse la resolución de concesión definitiva, la entidad beneficiaria podrá solicitar el pago anticipado de hasta el 60% de la subvención concedida para el proyectoempresa, con un máximo de 150.000 euros. No se deberá constituir garantía alguna. Cualquier pago quedará condicionado a que exista constancia de que la entidad beneficiaria cumple todos los requisitos señalados en el artículo 34 de la Ley 38/2003, de 17 de noviembre, General de Subvenciones.", "fragment": "19-4", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Tras dictarse la resolución de concesión definitiva, la entidad beneficiaria podrá solicitar el pago anticipado de hasta el 60% de la subvención concedida para el proyecto empresa, con un máximo de 150.000 euros. No se deberá constituir garantía alguna.", "fragment": "19-4"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "26-4"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "29-3"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "las ayudas objeto de esta convocatoria tienen la consideración de ayuda de estado y cumplen con todas las condiciones, tanto generales como específicas, contempladas en el capítulo I y en los artículos 4 y 22 del Reglamento (UE) n.º 651/2014, por lo que quedan exentas de la obligación de notificación prevista en el artículo 108, apartado 3, del Tratado.", "fragment": "29-3", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "29-3"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "2-4"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "29-4"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Con carácter general se podrá financiar hasta el 70% del presupuesto elegible de la actuación, sin contar la formación", "fragment": "7-1", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Con carácter general se podrá financiar hasta el 70% del presupuesto elegible de la actuación, sin contar la formación", "fragment": "7-1", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "7-2"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "19-4"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": {"texto": "Las ayudas de esta convocatoria consistirán en subvenciones.", "fragment": "7-1", "id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha"}}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "4-6"}]', '[{"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "8-1"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "8-2"}, {"id": "e568eaad-d004-4a6d-94a5-27ef138d0eca_ficha", "fragment": "8-3"}, {"id": "e568e