INSERT INTO public.ayudas_ref VALUES ('f5fe8536-9181-4958-97d0-06be78e0793c_1', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El Consejo de Administración del CDTI es el órgano competente para la aprobación o denegación de los proyectos y la concesión de las ayudas.", "fragment": "16-3", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "Los solicitantes deberán presentar la solicitud de ayuda (...) en la sede electrónica de CDTI.", "fragment": "14-3", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "Los proyectos se financiarán con fondos (...) del CDTI.", "fragment": "11-5", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}]', NULL, '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "14-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "14-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "2-7"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "6-3"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "6-5"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "14-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por proyecto es de 175.000 euros.", "fragment": "11-3"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El presupuesto mínimo financiable por proyecto es de 175.000 euros.", "fragment": "11-4"}}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El importe total de las ayudas de minimis concedidas por un Estado miembro a una «única empresa» no excederá de 300.000€ durante cualquier período de tres años.", "fragment": "13-5"}}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "Los proyectos de innovación tecnológica susceptibles de ayuda a través de la línea LIC tendrán una duración mínima de 9 meses y máxima de 18 meses, con un único hito y no podrán finalizar más allá del 31/12/2026, sin perjuicio de las posibles prórrogas, considerando las exigencias y plazos previstos en el Reglamento (UE) 2021/241 del Parlamento Europeo y del Consejo de 12 de febrero", "fragment": "11-4", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "11-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-2"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-4"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "La ayuda se compone de un tramo reembolsable y de un tramo no reembolsable (TNR). El tipo de interés aplicable es fijo, en el momento de la concesión de la ayuda, establecido en el momento de la concesión, y será de Euribor a un año + 0,75%.", "fragment": "12-4", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El anticipo será abonado una vez formalizado el contrato de préstamo.", "fragment": "22-5", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-2"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "22-5"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "16-5"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "22-4"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "ayudas de minimis, por lo que no constituyen ayuda de estado.", "fragment": "26-6", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "establecen en la normativa vigente de las ayudas de minimis concedidas a una única empresa.", "fragment": "12-3", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "no se podrán superar los límites máximos de ayuda que se establecen en la normativa vigente de las ayudas de minimis concedidas a una única empresa.", "fragment": "13-5", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "El importe total de las ayudas de minimis concedidas por un Estado miembro a una «única empresa» no excederá de 300.000€ durante cualquier período de tres años.", "fragment": "12-5", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "ayudas de minimis, por lo que no constituyen ayuda de estado.", "fragment": "26-6", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "se establecen en la normativa vigente de las ayudas de minimis concedidas a una única empresa", "fragment": "12-3", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "En ningún caso se podrán superar los límites máximos de ayuda que se establecen en la normativa vigente de las ayudas de minimis concedidas a una única empresa.", "fragment": "13-5", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "Podrán tener la condición de beneficiarias de las ayudas las empresas que estén válidamente constituidas, tengan personalidad jurídica propia y domicilio fiscal en España, y desarrollen el proyecto de innovación tecnológica en España.", "fragment": "5-1", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "4-3"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "8-2"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "4-3"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-2"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-3"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-3"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "12-2"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": {"texto": "Estos proyectos se financiarán mediante una Ayuda Parcialmente Reembolsable con una cobertura de hasta el 85% del presupuesto total aprobado por el Consejo de Administración del CDTI.", "fragment": "12-2", "id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha"}}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "13-3"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "6-3"}]', '[{"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-4"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-5"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-4"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-5"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-4"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "9-6"}, {"id": "f5fe8536-9181-4958-97d0-06be78e0793c_ficha", "fragment": "10-1"}]');


